def translate(word)
    if @dictionary.split.include?(word)
      return @dictionary.split(word)[1][0, 100]
    end
  end
def vocab_list
    input = gets.chomp
    word_array = input.split(" ").uniq
    vocabulary_hash = Hash.new(0)
    word_array.each do |word|
        vocabulary_hash[word] = translate(word)
    end
    vocabulary_hash.each do |k, v|
        if k && v && k.length > 4
        puts
        p k + " => " + v
        puts
        end
    end
    puts
    while input != "Y" || "n"
        puts "More? [Y/n]"
        input = gets.chomp
        if input == "Y"
            vocab_list()
        else
            break
        end
    end
end

vocab_list()